// Получаем данные из webhook и 3x-ui
const webhookData = $input.item.json.body;
const xuiData = $('Get Client Data from 3x-ui').item.json;

// Находим клиента по email или UUID
let clientData = null;
if (xuiData && Array.isArray(xuiData)) {
  clientData = xuiData.find(c => 
    c.email === webhookData.email || 
    c.id === webhookData.uuid
  );
} else if (xuiData && xuiData.id) {
  clientData = xuiData;
}

// Формируем объект пользователя
const userData = {
  id: webhookData.userId,
  email: webhookData.email || clientData?.email || '',
  uuid: webhookData.uuid || clientData?.id || '',
  name: clientData?.email?.split('@')[0]?.replace(/_/g, ' ') || webhookData.email?.split('@')[0] || 'User',
  vpnUuid: clientData?.id || webhookData.uuid || '',
  plan: clientData?.plan || 'free',
  expiresAt: clientData?.expiryTime > 0 ? new Date(clientData.expiryTime).toISOString() : null,
  devices: clientData?.limitIp || 1,
  status: clientData?.enable !== false ? 'active' : 'inactive',
  // Дополнительные поля из 3x-ui
  totalGB: clientData?.totalGB || 0,
  up: clientData?.up || 0,
  down: clientData?.down || 0,
};

return {
  json: {
    success: true,
    user: userData
  }
};
